mapper.select:
SELECT COUNT(1) FROM tb_article
draft_flag = true AND recycle_flag = false
recycle_flag = true AND deleted_flag = false
deleted_flag = true
draft_flag = false
AND user_id = #{userId}
AND user_id = #{condition.userId}
AND category_id = #{condition.categoryId}
AND article_title LIKE concat('%', #{condition.keywords}, '%')
AND id IN ( SELECT ata.article_id FROM tb_article_tag ata WHERE ata.tag_id IN
#{tagId}
)

SELECT a.id,a.user_id,ua.username, a.article_title,a.top_flag,a.public_flag, a.hidden_flag,a.commentable_flag,a.publish_time, a.update_time,c.category_name,ata.tag_name tagNameList FROM tb_article a LEFT JOIN tb_category c ON a.category_id = c.id LEFT JOIN (SELECT article_id, GROUP_CONCAT(t.tag_name) tag_name FROM tb_article_tag ata LEFT JOIN tb_tag t ON ata.tag_id = t.id WHERE ata.deleted_flag = FALSE AND t.deleted_flag = FALSE GROUP BY article_id) ata ON a.id = ata.article_id LEFT JOIN tb_user_auth ua ON a.user_id = ua.user_id
a.draft_flag = true AND a.recycle_flag = false
a.recycle_flag = true AND a.deleted_flag = false
a.deleted_flag = true
a.draft_flag = false
AND a.user_id = #{userId}
AND a.user_id = #{condition.userId}
AND a.category_id = #{condition.categoryId}
AND a.article_title LIKE concat('%', #{condition.keywords}, '%')
AND a.id IN ( SELECT ata.article_id FROM tb_article_tag ata WHERE ata.tag_id IN
#{tagId}
)
ORDER BY a.top_flag DESC, a.id DESC LIMIT #{condition.current}, #{condition.size}

SELECT a.id,a.user_id,a.category_id, a.article_title,a.article_cover,CASE WHEN ((a.public_flag = false OR c.public_flag = false) AND #{loginFlag}) THEN "" ELSE a.article_content END AS article_content, a.top_flag,a.public_flag,a.hidden_flag, a.publish_time,c.category_name,ata.tag tagList, c.public_flag categoryPublicFlag FROM tb_article a LEFT JOIN tb_category c ON a.category_id = c.id LEFT JOIN (SELECT article_id, GROUP_CONCAT(CONCAT(t.id, "=", t.tag_name)) tag FROM tb_article_tag ata LEFT JOIN tb_tag t ON ata.tag_id = t.id WHERE ata.deleted_flag = FALSE AND t.deleted_flag = FALSE GROUP BY article_id) ata ON a.id = ata.article_id
a.draft_flag = false AND a.user_id = #{condition.userId}
AND a.hidden_flag = false
ORDER BY a.top_flag DESC, a.id DESC LIMIT #{condition.current}, #{condition.size}

SELECT a.id,a.user_id,a.category_id, a.article_title,a.article_cover,CASE WHEN (a.public_flag = false AND #{flag}) THEN "" ELSE a.article_content END AS article_content, a.public_flag,a.hidden_flag,a.commentable_flag, a.update_time,a.publish_time,c.category_name, ata.tag tagList FROM tb_article a LEFT JOIN tb_category c ON a.category_id = c.id LEFT JOIN (SELECT article_id, GROUP_CONCAT(CONCAT(t.id, "=", t.tag_name)) tag FROM tb_article_tag ata LEFT JOIN tb_tag t ON ata.tag_id = t.id WHERE ata.deleted_flag = FALSE AND t.deleted_flag = FALSE GROUP BY article_id) ata ON a.id = ata.article_id
a.id = #{id} AND a.draft_flag = false AND a.user_id = #{bloggerId}
AND a.hidden_flag = false
